mapper.insert:
INSERT INTO TB_CALENDAR_EVENT (
    EVENT_ID, EVENT_TITLE, EVENT_WRITER, EVENT_DESCRIPTION, START_TIME, END_TIME,
    CATEGORY, EVENT_STATUS, CREATED_TIME, TEXT_COLOR, BACKGROUND_COLOR, BORDER_COLOR
)
VALUES (
    SEQ_CALENDAR.NEXTVAL, #{eventTitle}, #{eventWriter}, #{eventDescription},
    TO_DATE(#{startTime}, 'YYYY-MM-DD'), TO_DATE(#{endTime}, 'YYYY-MM-DD'),
    #{category}, #{eventStatus}, SYSDATE, #{textColor}, #{backgroundColor}, #{borderColor}
)

mapper.select:
SELECT PRJCT_NO AS eventId, PRJCT_NM AS eventTitle, CHARGER AS eventWriter, 'ÌîÑÎ°úÏ†ùÌä∏' AS category, PRJCT_CTGRY AS eventDescription, TO_CHAR(TO_DATE(PRJCT_BGNDE, 'YYYYMMDD'), 'YYYY-MM-DD') AS startTime, TO_CHAR(TO_DATE(PRJCT_ENDDE, 'YYYYMMDD'), 'YYYY-MM-DD') AS endTime, '01' AS eventStatus, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') AS createdTime FROM TB_PROJECT WHERE PRJCT_NO = #{prjctNo}
UNION ALL
SELECT PROCS_NO AS eventId, PROCS_NM AS eventTitle, CCPY_CODE AS eventWriter, 'Í≥µÏ†ï' AS category, PROCS_CN AS eventDescription, TO_CHAR(PROCS_BGNDE, 'YYYY-MM-DD') AS startTime, TO_CHAR(PROCS_ENDDE, 'YYYY-MM-DD') AS endTime, PROGRS_STTUS AS eventStatus, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') AS createdTime FROM TB_PROCESS WHERE PRJCT_NO = #{prjctNo}
